    /**
     * @static
     * @param Horoscope $zodiac
     * @return string
     */
    public static function getText($zodiac)
    {
        if ($zodiac->onYear())
            return self::getYearText($zodiac->zodiac);
        if ($zodiac->onMonth())
            return self::getMonthText($zodiac->zodiac);

        return self::getDateText($zodiac->zodiac, $zodiac->date);
    }

